  /**
   * @covers mychaelstyle\storage\Provider::getInstance
   */
  public function testGetInstance()
  {
    $def = $this->dsnMap['Local'];
    // valid
    $this->object = Provider::getInstance($def['dsn'],$def['options']);
    $this->assertEquals('mychaelstyle\\storage\\providers\\Local',get_class($this->object));
