  public function testEncode(){
    $expected = $this->example_text;
    $path     = $this->org_example;
    $to       = tempnam(DIR_TEST.'/tmp','encoded_');
    $recover  = tempnam(DIR_TEST.'/tmp','encoded_');
    $this->object->encode($path,$to);
